<script>
export default {
  name: "SideBar",
  props: ['isOpen'],
  data(){
    return{
      username: 'Бекарыс Алашыбаев',
      currentUser: {
        gmail: '',
        avatar: '',
        first_name: '',
        last_name: '',
      }
    }
  },
  created() {
    this.authMe()
  },
  methods:{
    getSubstringUsername(){
      // if (this.username.length > 16){
      //   return this.username.substring(0,15)+"..."
      // }
      if (!this.currentUser.first_name){
        return "test test"
      }
      return this.currentUser.first_name + ' ' + this.currentUser.last_name
    },
    getFirstLetter(){
      // return this.currentUser.first_name.charAt(0).toUpperCase() + this.currentUser.last_name.charAt(0).toUpperCase()
      return "AA"
    },
    async logOut() {
      await this.$auth.logout()
    },
    async authMe(){
      try {
        let data = (await this.$axios.get('user/me/')).data
        this.currentUser = data
        await this.$store.dispatch('user/authUser', this.currentUser)
      }
      catch (e) {
        console.log(e)
        this.$toast.error('Error while authenticating')
        await this.logOut()
      }
    },
  }
}
</script>

